import{a as l}from"./supabase-BgHskl7U.js";/* empty css                 */const s=document.getElementById("login-form"),e=document.getElementById("login-msg");document.querySelectorAll(".pw-toggle").forEach(o=>{o.addEventListener("click",()=>{const t=o.parentElement.querySelector("input"),r=o.querySelector("i");t.type==="password"?(t.type="text",r.classList.replace("fa-eye","fa-eye-slash")):(t.type="password",r.classList.replace("fa-eye-slash","fa-eye"))})});s.addEventListener("submit",async o=>{o.preventDefault(),e.textContent="",e.style.color="#b00020";const t=s.email.value.trim(),r=s.password.value;if(!t||!r){e.textContent="Enter email and password.";return}try{const{data:n,error:a}=await l.auth.signInWithPassword({email:t,password:r});if(a){e.textContent=a.message;return}e.style.color="#2a8f3a",e.textContent="Login successful! Redirecting...",setTimeout(()=>{window.location.href="index.html"},800)}catch(n){console.error("Login error:",n),e.textContent="An unexpected error occurred. Please try again."}});
