import{a as d}from"./supabase-BgHskl7U.js";/* empty css                 */const s=document.getElementById("register-form"),e=document.getElementById("register-msg"),c=document.querySelector(".pw-toggle");c.addEventListener("click",()=>{const o=document.getElementById("password"),t=c.querySelector("i");o.type==="password"?(o.type="text",t.classList.remove("fa-eye"),t.classList.add("fa-eye-slash")):(o.type="password",t.classList.remove("fa-eye-slash"),t.classList.add("fa-eye"))});s.addEventListener("submit",async o=>{o.preventDefault(),e.textContent="",e.style.color="#b00020";const t=s.name.value.trim(),a=s.email.value.trim(),r=s.password.value,l=s.confirmPassword.value;if(!t||!a||!r){e.textContent="Please fill required fields.";return}if(r.length<6){e.textContent="Password must be at least 6 characters.";return}if(r!==l){e.textContent="Passwords do not match.";return}try{const{data:n,error:i}=await d.auth.signUp({email:a,password:r,options:{data:{name:t}}});if(i){e.textContent=i.message;return}e.style.color="#2a8f3a",e.textContent="Registration successful! Redirecting to login...",s.reset(),setTimeout(()=>{window.location.href="login.html"},1500)}catch(n){console.error("Registration error:",n),e.textContent="An unexpected error occurred. Please try again."}});
